import AbstractLayer from './AbstractLayer';
import { ColorRepresentation, IUniform } from 'three';
import { BaseLayerProps, LayerBlendMode } from '../types';
export default class BaseLayer extends AbstractLayer {
    name: string;
    protected uuid: string;
    uniforms: {
        [key: string]: IUniform<any>;
    };
    constructor(props?: BaseLayerProps);
    getFragmentVariables(): string;
    getFragmentBody(e: string): string;
    set color(v: ColorRepresentation);
    get color(): ColorRepresentation;
    set alpha(v: number);
    get alpha(): number;
    set mode(v: LayerBlendMode);
    get mode(): LayerBlendMode;
}
