import { DepthProps, BlendMode } from '../types';
import { Vector3, ColorRepresentation, IUniform } from 'three';
import Abstract from './Abstract';
export default class Depth extends Abstract {
    name: string;
    mode: BlendMode;
    protected uuid: string;
    uniforms: {
        [key: string]: IUniform<any>;
    };
    constructor(props?: DepthProps);
    getVertexVariables(): string;
    getVertexBody(e: string): string;
    getFragmentVariables(): string;
    getFragmentBody(e: string): string;
    set alpha(v: number);
    get alpha(): number;
    set near(v: number);
    get near(): number;
    set far(v: number);
    get far(): number;
    set origin(v: Vector3);
    get origin(): Vector3;
    set colorA(v: ColorRepresentation);
    get colorA(): ColorRepresentation;
    set colorB(v: ColorRepresentation);
    get colorB(): ColorRepresentation;
    set isVector(v: boolean);
    get isVector(): boolean;
}
