import { ColorRepresentation, IUniform } from 'three';
import { BlendMode, NoiseProps } from '../types';
import Abstract from './Abstract';
export default class Noise extends Abstract {
    name: string;
    mode: BlendMode;
    protected uuid: string;
    uniforms: {
        [key: string]: IUniform<any>;
    };
    constructor(props?: NoiseProps);
    getVertexVariables(): string;
    getVertexBody(e: string): string;
    getFragmentVariables(): string;
    getFragmentBody(e: string): string;
    set alpha(v: number);
    get alpha(): number;
    set color(v: ColorRepresentation);
    get color(): ColorRepresentation;
    set scale(v: number);
    get scale(): number;
}
